import { useState } from "react";

/* eslint-disable no-const-assign */
function App() {
  return <Myapp />;
}

function Myapp() {
  const date = new Date().toDateString();

  const [start2, setdata] = useState(0);
  // let start2 = 0;

  let start1 = 0;

  function handleplus() {
    start1 += 1;
    console.log(start1);
  }
  function count() {
    // start2 += 1;
    setdata((start1 += 1));
    console.log(start2);
  }
  function handlemwens() {
    if (start1 >= 1) start1 -= 1;
    console.log(start1);
  }
  function countmwens() {
    if (start2 >= 1) start2 -= 1;
    console.log(start2);
  }

  return (
    <div>
      <button onClick={handlemwens}>-</button>
      <span> Step: 1 </span>
      <button onClick={handleplus}>+</button>
      <br></br>
      <button onClick={countmwens}>-</button>
      <span> Count: {start1} </span>
      <button onClick={count}>+</button>
      <>
        <p>
          {" "}
          {`${start2} days from`} today is {date}{" "}
        </p>
      </>
    </div>
  );
}
export default App;
