import { useState } from "react";
function App() {
  return <Myapp />;
}

function Myapp() {
  // const date = new Date().toDateString();

  let [start2, setdata2] = useState(0);

  let [start1, setdata1] = useState(1);

  function stepplus() {
    const step = (start1 += 1);
    if (step > 0) setdata1(step);
  }
  function countplus() {
    //
    setdata2((start2 += 1));
  }
  function stepmwens() {
    if (start1 > 1) setdata1((start1 -= 1));
  }
  function countmwens() {
    //
    if (start2 >= 1) setdata2((start2 -= 1));
    // console.log(start1);
  }

  return (
    <div>
      <button onClick={stepmwens}>-</button>
      <span> Step: {step} </span>
      <button onClick={stepplus}>+</button>
      <br></br>
      <button onClick={countmwens}>-</button>
      <span> Count: {start2} </span>
      <button onClick={countplus}>+</button>
      <>
        <p>
          {`${start2 && start1} days from`}

          {}
          {/* {`today is ${date}`} */}
        </p>
      </>
    </div>
  );
}
export default App;
